$(function(){console.log("ready!"),$(".pagination").find("a").click(function(){appendedURI="";var e=decodeURIComponent($(this).attr("href"));if(e.indexOf("checked=")>=0)var n=e;else var n=e+"&checked=";$("input[type = checkbox]:checked").each(function(){appendedURI=appendedURI+$(this).attr("name").substring(8,$(this).attr("name").length-1)+"+"});var t=n.indexOf("checked=");n=n.slice(0,t+8)+appendedURI+n.slice(t+8),$(this).attr("href",n)})});